mapper.select:

SELECT *
FROM ohjooyeo.ORDER
WHERE worship_id like #{worshipId}
AND title like #{findOrder}


SELECT ORDER.presenter, ORDER.detail, ORDER.title, ORDER.order, ORDER.order_id orderId, ORDER.type, ORDER.standup_yn standupYn
FROM ohjooyeo.ORDER
WHERE worship_id like #{worshipId}
AND church_id like #{churchId}
ORDER BY ORDER.order


SELECT lp.phrase
FROM ohjooyeo.LAUNCH_PHRASE lp
WHERE lp.user_id like #{userId} 
AND #{date} between lp.start_date and lp.end_date;

update.foreach:

UPDATE ohjooyeo.ORDER 
SET ohjooyeo.ORDER.order = #{vo.order}, 
	ohjooyeo.ORDER.type = #{vo.type}, 
	ohjooyeo.ORDER.title = #{vo.title}, 
	ohjooyeo.ORDER.detail = #{vo.detail}, 
	ohjooyeo.ORDER.presenter = #{vo.presenter},
	ohjooyeo.ORDER.standup_yn = #{vo.standupYn}
WHERE order_id = #{vo.orderId}
AND church_id = #{vo.churchId}
AND worship_id = #{vo.worshipId}

delete.foreach:

DELETE FROM ohjooyeo.ORDER
WHERE worship_id = #{worshipId} 
AND order_id = #{id}

mapper.delete:
DELETE FROM ohjooyeo.ORDER
WHERE church_id = #{churchId} 
AND worship_id = #{worshipId}
